mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj,t2.cscyj,t2.stz from GCGL_GCGZGJ t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and t1.sfsj <=#{sfsj}

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_GCGZGJ 
where 1=1 and sfsj <=#{sfsj}

	and jhid =#{jhid}

select * from (
select m.*,rownum rn from (
	select t1.*,t2.tbr,t2.tbsj,t2.tbyf,t2.cgsdwzj,t2.cscyj,t2.stz from GCGL_GCGZGJ t1 left join GCGL_CGS t2 on t1.jhid=t2.jhid and t1.sbyf=t2.tbyf
	where 1=1 and sfsj=7 

	and t1.jhid =#{jhid}

order by to_number(substr(sbyf,0,4))desc,to_number(replace(sbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_GCGZGJ 
where 1=1 and sfsj=7 

	and jhid =#{jhid}

