mapper.select:
select * from (
select m.*,rownum rn from (
	select t1.*,t2.sbsj,t2.sbyf from GCGL_CGS t1 left join GCGL_GCGZGJ t2 on t1.jhid=t2.jhid and t1.tbyf=t2.sbyf
	where 1=1

	and t1.jhid =#{jhid}

order by to_number(substr(tbyf,0,4))desc,to_number(replace(tbyf,'-','')) desc
)  m
	where rownum<=(#{page} * #{rows})
) 
	where RN>((#{page} - 1) * #{rows})

select count(*) from GCGL_CGS 
where 1=1

	and jhid =#{jhid}

