mapper.select:
		select * from 
			(
				select rownum as num,t.* from 
				(
					select jh.id,jh.sckid,jh.jhnf,jh.jhkgsj,jh.jhwgsj,jh.pfztz,jh.jhsybbzje,jh.jhsydfzczj,jh.sfsqablbz,
					jh.sbzt,jh.spzt,jh.jh_sbthcd,jh.kgzt,jh.jgzt,sc.bzls as sfylsjl,
               		lx.gydw,lx.xzqhmc,lx.xzqhdm,lx.lxbm,lx.lxmc,sc.scqdzh as qdzh,sc.sczdzh as zdzh,sc.scyhlc as yhlc
                	from plan_abgc jh left join sck_abgc sc on (jh.sckid=sc.sckid) 
					left join xmk_abgc lx on (sc.xmkid=lx.id) where 1=1 
					
						and jh.jh_sbthcd=#{jh.jh_sbthcd}
					
				    
						and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
					
					
			 			and jh.kgzt=#{jh.kgzt}
			 		
			 		
			 			and jh.jgzt=#{jh.jgzt}
			 		
				    
				 		and jh.jhnf=#{jh.jhnf}
				 	
				 	
				 		and jh.sbzt=#{jh.sbzt}
				 	
				 	
				 		and jh.spzt=#{jh.spzt}
				 	
				 	
				 		and sc.bzls=#{jh.sfylsjl}
				 	
				 	
				 		and ${lx.gydwbm}
				 	
				 	
				 		and ${lx.xzqhdm}
				 	
				 	
				 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
				 	
				 	
				 		and lx.lxbm like #{lx.lxbm}||'%'
				 	
				 	
				 		and lx.lxjsdj = #{lx.lxjsdj}
				 	
				 	
				 		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
				 	
	    			order by jh.jhnf desc,jh.jh_sbthcd
	    		) t order by t.jhnf desc,t.jh_sbthcd
    		)
    	where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

		select * from 
			(
				select rownum as num,t.* from 
				(
					select * from plan_abgc jh left join sck_abgc sc on (jh.sckid=sc.sckid) 
					left join xmk_abgc lx on (sc.xmkid=lx.id) where 1=1 
					
						and jh.jh_sbthcd=#{jh.jh_sbthcd}
					
					
			 			and jh.kgzt=#{jh.kgzt}
			 		
			 		
			 			and jh.jgzt=#{jh.jgzt}
			 		
				    
				 		and jh.jhnf=#{jh.jhnf}
				 	
				 	
				 		and sc.bzls=#{jh.sfylsjl}
				 	
				 	
				 		and ${lx.gydwbm}
				 	
				 	
				 		and ${lx.xzqhdm}
				 	
				 	
				 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
				 	
				 	
				 		and lx.lxbm like #{lx.lxbm}||'%'
				 	
				 	
				 		and lx.lxjsdj = #{lx.lxjsdj}
				 	
				 	
				 		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
				 	
	    			order by jh.jhnf desc,jh.jh_sbthcd) t order by t.jhnf desc ,t.jh_sbthcd
    		)

	select * from plan_abgc jh left join sck_abgc sc on (jh.sckid=sc.sckid) 
	left join xmk_abgc lx on (sc.xmkid=lx.id) where 1=1 
	
		and jh.jhnf=#{jh.jhnf}
	
	
		and jh.jh_sbthcd=#{jh.jh_sbthcd}
	
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
	
		and jh.sbzt=#{jh.sbzt}
	
	
		and jh.spzt=#{jh.spzt}
	
	
		and ${lx.gydwbm}
	
	
		and ${lx.xzqhdm}
	
	
		and lx.lxmc like '%'||#{lx.lxmc}||'%'
	
	
		and lx.lxbm like #{lx.lxbm}||'%'
	
	
		and lx.lxjsdj = #{lx.lxjsdj}
	
	
		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
	
    order by jh.jhnf,jh.jh_sbthcd

select count(*) from plan_abgc jh left join sck_abgc sc on (jh.sckid=sc.sckid) 
	left join xmk_abgc lx on (sc.xmkid=lx.id) where 1=1 

	and jh.jh_sbthcd=#{jh.jh_sbthcd}


	and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)


	and jh.kgzt=#{jh.kgzt}


	and jh.jgzt=#{jh.jgzt}


	and jh.jhnf=#{jh.jhnf}


	and jh.sbzt=#{jh.sbzt}


	and jh.spzt=#{jh.spzt}


	and sc.bzls=#{jh.sfylsjl}


	and ${lx.gydwbm}


	and ${lx.xzqhdm}


	and lx.lxmc like '%'||#{lx.lxmc}||'%'


	and lx.lxbm like #{lx.lxbm}||'%'


	and lx.lxjsdj = #{lx.lxjsdj}


	and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})

	
	select id,sckid,jhnf,jhkgsj,jhwgsj,sjdw,sjpfdw,pfwh, pfsj,pfztz,jhsybbzje,jhsydfzczj,sfsqablbz,ablbzsqwh, remarks, tbsj, tbbm, spzt,spbm,
spsj,sbzt,sbbm,sbsj,xdsj,gkbgmc,gkbglj,sjsgtmc,sjsgtlj,sjkgsj,sjwgsj,sgdw, jldw, htje,sgxkwj,jgtcwj, jgyswj,wjsczt,kgzt,jgzt,wjgyy,
yjwgsj, jhwc_c,jsdw,jhxdwh,xdzt,xfzt,xfsj,xfbm,wjzt,sfylsjl from Plan_Abgc jh where jh.id=#{id}

		select  decode(jh.sbzt,0,'未上报',decode(jh.spzt,0,'上报待审批','已审批')) v_0,jh.jhnf v_1,to_char(jh.jhkgsj,'yyyy-mm-dd') v_2,to_char(jh.jhwgsj,'yyyy-mm-dd') v_3,lx.gydw v_4,lx.xzqhmc v_5,lx.lxbm v_6,lx.lxmc v_7,lx.qdzh v_8,lx.zdzh v_9,lx.yhlc v_10,jh.pfztz v_11
		 from plan_abgc jh
  		left join sck_abgc sc on (jh.sckid = sc.sckid)
  		left join xmk_abgc lx on (sc.xmkid = lx.id) where 1=1 
		
						and jh.jh_sbthcd=#{jh.jh_sbthcd}
					
				    
						and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
					
					
			 			and jh.kgzt=#{jh.kgzt}
			 		
			 		
			 			and jh.jgzt=#{jh.jgzt}
			 		
				    
				 		and jh.jhnf=#{jh.jhnf}
				 	
				 	
				 		and jh.sbzt=#{jh.sbzt}
				 	
				 	
				 		and jh.spzt=#{jh.spzt}
				 	
				 	
				 		and sc.bzls=#{jh.sfylsjl}
				 	
				 	
				 		and ${lx.gydwbm}
				 	
				 	
				 		and ${lx.xzqhdm}
				 	
				 	
				 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
				 	
				 	
				 		and lx.lxbm like #{lx.lxbm}||'%'
				 	
				 	
				 		and lx.lxjsdj = #{lx.lxjsdj}
				 	
				 	
				 		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
				 	
		order by jh.jhnf desc

mapper.update:
update plan_abgc set jhnf=#{jhnf}
	,jhkgsj=#{jhkgsj}
	,jhwgsj=#{jhwgsj}
	,xdsj=#{xdsj}
	,pfsj=#{pfsj}
	,jhwc_c=#{jhwc_c}
	,sjdw=#{sjdw}
	,sjpfdw=#{sjpfdw}
	,pfwh=#{pfwh}
	,pfztz=#{pfztz}
	,jhsybbzje=#{jhsybbzje}
	,jhsydfzczj=#{jhsydfzczj}
	,jhxdwh=#{jhxdwh}
	,sfsqablbz=#{sfsqablbz}
	,ablbzsqwh=#{ablbzsqwh}
	,remarks=#{remarks}
where id=#{id}

update SCK_ABGC set sckid=#{sckid}
,scqdzh=#{scqdzh}
,sczdzh=#{sczdzh}
,sczlc=#{sczlc}
,scyhlc=#{scyhlc}
,fapgdw=#{fapgdw}
,fascdw=#{fascdw}
,spwh=#{spwh}
,tzgs=#{tzgs}
,faspsj=#{faspsj}
,jsxz=#{jsxz}
,jsnr=#{jsnr}
,scbz=#{scbz}
where sckid=#{sckid}

mapper.select:
select count(id) as id,sum(zlc) as qzlc,sum(yhlc) as yhlc,
sum(pfztz) as pfztz,sum(jhsybbzje) as jhsybbzje,sum(jhsydfzczj) as jhsydfzczj from(
	select count(jh.id) as id,sum(sc.sczlc) as zlc,sum(jh.jhzlc) as yhlc,
	sum(jh.pfztz) as pfztz,sum(jh.jhsybbzje) as jhsybbzje,sum(jh.jhsydfzczj) as jhsydfzczj
	from xmk_abgc lx right join sck_abgc sc on (lx.id=sc.xmkid) right join plan_abgc jh on (sc.sckid=jh.sckid)
	where 1=1
	
				and jh.jh_sbthcd=#{jh.jh_sbthcd}
			
		    
				and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
			
			
	 			and jh.kgzt=#{jh.kgzt}
	 		
	 		
	 			and jh.jgzt=#{jh.jgzt}
	 		
		    
		 		and jh.jhnf=#{jh.jhnf}
		 	
		 	
		 		and jh.sbzt=#{jh.sbzt}
		 	
		 	
		 		and jh.spzt=#{jh.spzt}
		 	
		 	
		 		and sc.bzls=#{jh.sfylsjl}
		 	
		 	
		 		and ${lx.gydwbm}
		 	
		 	
		 		and ${lx.xzqhdm}
		 	
		 	
		 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
		 	
		 	
		 		and lx.lxbm like #{lx.lxbm}||'%'
		 	
		 	
		 		and lx.lxjsdj = #{lx.lxjsdj}
		 	
		 	
		 		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
		 	 group by jh.id)

select nvl(sum(r.jhsybbzje),0) as text,nvl(sum(r.btz+r.qttz),0) as parent from ( select jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybbzje,sum(nvl(gcgl.wc_btz,0)) as btz,sum(nvl(gcgl.wc_qttz,0)) as qttz from gcgl_abgc gcgl right join (select jh.id,jh.kgzt,jh.jgzt,jh.jhsybbzje from plan_abgc jh left join sck_abgc sc on (jh.sckid=sc.sckid) left join xmk_abgc lx on (sc.xmkid=lx.id) where jh.spzt='1' and jh.jh_sbthcd='6'
and lx.xzqhdm like #{xzqhdm}
) jh_lx on gcgl.jhid=jh_lx.id group by jh_lx.id,jh_lx.kgzt,jh_lx.jgzt,jh_lx.jhsybbzje) r

		select * from 
			(
				select rownum as num,t.* from 
				(
					select lx.lxbm,lx.lxmc,lx.gydw,lx.xzqhmc,sc.scqdzh,sc.sczdzh,sc.sczlc,sc.scyhlc,sc.fapgdw,sc.fascdw,sc.faspsj,sc.spwh,sc.tzgs,sc.jsxz,sc.jsnr,sc.scbz,
					jh.id,jh.sckid,jh.jhnf,to_char(jh.jhkgsj,'yyyy-mm-dd') jhkgsj1,to_char(jh.jhwgsj,'yyyy-mm-dd') jhwgsj1,jh.jhwc_c,jh.sjdw,jh.sjpfdw,jh.pfwh,to_char(jh.pfsj,'yyyy-mm-dd') pfsj1,jh.pfztz,jh.jhsybbzje,jh.jhsydfzczj,jh.sfsqablbz,jh.ablbzsqwh,jh.remarks
					from plan_abgc jh left join sck_abgc sc on (jh.sckid=sc.sckid) 
					left join xmk_abgc lx on (sc.xmkid=lx.id) where 1=1
					
						and jh.jh_sbthcd=#{jh.jh_sbthcd}
					
				    
						and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
					
					
			 			and jh.kgzt=#{jh.kgzt}
			 		
			 		
			 			and jh.jgzt=#{jh.jgzt}
			 		
				    
				 		and jh.jhnf=#{jh.jhnf}
				 	
				 	
				 		and jh.sbzt=#{jh.sbzt}
				 	
				 	
				 		and jh.spzt=#{jh.spzt}
				 	
				 	
				 		and sc.bzls=#{jh.sfylsjl}
				 	
				 	
				 		and ${lx.gydwbm}
				 	
				 	
				 		and ${lx.xzqhdm}
				 	
				 	
				 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
				 	
				 	
				 		and lx.lxbm like #{lx.lxbm}||'%'
				 	
				 	
				 		and lx.lxjsdj = #{lx.lxjsdj}
				 	
				 	
				 		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
				 	
	    			order by jh.jhnf desc,jh.jh_sbthcd
	    		) t order by t.jhnf desc
    		)
