mapper.select:
		select * from 
			(
				select rownum as num,t.* from
				(
					select jh.id,jh.sckid,jh.jh_sbthcd,jh.sbnf,jh.sbzt,jh.spzt,jh.kgzt,jh.jgzt,jh.jhkgsj,jh.jhwgsj,jh.pfztz,sc.bzls as sfylsjl,lx.gydw,lx.gydwbm,lx.xzqhdm,lx.xzqhmc,lx.lxmc,lx.lxbm,lx.qlmc,lx.qlbh 
					from plan_wqgz jh left join sck_wqgz sc on (jh.sckid=sc.sckid) 
					left join xmk_wqgz lx on (sc.xmkid=lx.id) where 1=1
	 				
						and jh.jh_sbthcd=#{jh.jh_sbthcd}
					
				    
						and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
					
	 				
	 					and jh.sbnf=#{jh.sbnf}
	 				
	 				
	 					and jh.sbzt=#{jh.sbzt}
	 				
	 				
	 					and jh.kgzt=#{jh.kgzt}
	 				
	 				
	 					and jh.jgzt=#{jh.jgzt}
	 				
	 				
	 					and jh.spzt=#{jh.spzt}
	 				
	 				
			 			and sc.bzls=#{jh.sfylsjl}
				 	
	 				
	 					and ${lx.gydwbm}
	 				
	 				
	 					and ${lx.xzqhdm}
	 				
	 				
	 					and lx.lxmc like '%'||#{lx.lxmc}||'%'
	 				
	 				
	 					and lx.lxbm like #{lx.lxbm}||'%'
	 				
	 				
	 					and lx.jsdj = #{lx.jsjd}
	 				
	 				
	 					and lx.qlmc like '%'||#{lx.qlmc}||'%'
	 				
	 				
	 					and lx.akjfl like #{lx.akjfl}||'%'
	 				
	 				
				 		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
				 	
	 				order by jh.sbnf desc,jh.jh_sbthcd
	 			) t order by t.sbnf desc,t.jh_sbthcd
 			)
 		where  num <= (#{page} * #{rows}) and num>(#{page}-1)*#{rows}

		select * from 
			(
				select rownum as num,t.* from
				(
					select * from plan_wqgz jh left join sck_wqgz sc on (jh.sckid=sc.sckid) 
					left join xmk_wqgz lx on (sc.xmkid=lx.id) where 1=1
					
	 					and jh.sbnf=#{jh.sbnf}
	 				
	 				
						and jh.jh_sbthcd=#{jh.jh_sbthcd}
					
	 				
	 					and jh.kgzt=#{jh.kgzt}
	 				
	 				
	 					and jh.jgzt=#{jh.jgzt}
	 				
	 				
			 			and sc.bzls=#{jh.sfylsjl}
				 	
	 				
	 					and ${lx.gydwbm}
	 				
	 				
	 					and ${lx.xzqhdm}
	 				
	 				
	 					and lx.lxmc like '%'||#{lx.lxmc}||'%'
	 				
	 				
	 					and lx.lxbm like #{lx.lxbm}||'%'
	 				
	 				
	 					and lx.jsdj = #{lx.jsjd}
	 				
	 				
	 					and lx.qlmc like '%'||#{lx.qlmc}||'%'
	 				
	 				
	 					and lx.akjfl like #{lx.akjfl}||'%'
	 				
	 				
				 		and lx.xzqhdm in (select t.xzqhdm from xtgl_xzqhtsdq t where t.parent =#{lx.tsdq})
				 	
	 				order by jh.sbzt,jh.spzt,jh.sbnf desc
	 			) t
 			)

	select * from plan_wqgz jh left join sck_wqgz sc on (jh.sckid=sc.sckid) 
	left join xmk_wqgz lx on (sc.xmkid=lx.id) where 1=1
	
		and jh.jh_sbthcd=#{jh.jh_sbthcd}
	
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
	
 		and jh.sbnf=#{jh.sbnf}
 	
 	
 		and jh.sbzt=#{jh.sbzt}
 	
 	
 		and jh.spzt=#{jh.spzt}
 	
 	
 		and ${lx.gydwbm}

	select count(*) from plan_wqgz jh left join sck_wqgz sc on (jh.sckid=sc.sckid) 
	left join xmk_wqgz lx on (sc.xmkid=lx.id) where 1=1
 	
		and jh.jh_sbthcd=#{jh.jh_sbthcd}
	
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
 	
 		and jh.sbnf=#{jh.sbnf}
 	
 	
 		and jh.sbzt=#{jh.sbzt}
 	
 	
 		and jh.kgzt=#{jh.kgzt}
 	
 	
 		and jh.jgzt=#{jh.jgzt}
 	
 	
 		and jh.spzt=#{jh.spzt}
 	
 	
		and sc.bzls=#{jh.sfylsjl}
	
 	
 		and ${lx.gydwbm}
 	
 	
 		and ${lx.xzqhdm}
 	
 	
 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
 	
 	
 		and lx.lxbm like #{lx.lxbm}||'%'
 	
 	
 		and lx.jsdj = #{lx.jsjd}
 	
 	
 		and lx.qlmc like '%'||#{lx.qlmc}||'%'
 	
 	
 		and lx.akjfl like #{lx.akjfl}||'%'

	select count(id) as id,sum(pfztz) as pfztz,sum(jhsybzje) as jhsybzje,
	sum(jhsydfzcje) as jhsydfzcje from (
		select count(jh.id) as id,sum(jh.pfztz) as pfztz,sum(jh.jhsybzje) as jhsybzje,
		sum(jh.jhsydfzcje) as jhsydfzcje 
		from xmk_wqgz lx right join sck_wqgz sc on (lx.id=sc.xmkid) 
		right join plan_wqgz jh on (sc.sckid=jh.sckid)
		where 1=1
		
		and jh.jh_sbthcd=#{jh.jh_sbthcd}
	
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
 	
 		and jh.sbnf=#{jh.sbnf}
 	
 	
 		and jh.sbzt=#{jh.sbzt}
 	
 	
 		and jh.kgzt=#{jh.kgzt}
 	
 	
 		and jh.jgzt=#{jh.jgzt}
 	
 	
 		and jh.spzt=#{jh.spzt}
 	
 	
		and sc.bzls=#{jh.sfylsjl}
	
 	
 		and ${lx.gydwbm}
 	
 	
 		and ${lx.xzqhdm}
 	
 	
 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
 	
 	
 		and lx.lxbm like #{lx.lxbm}||'%'
 	
 	
 		and lx.jsdj = #{lx.jsjd}
 	
 	
 		and lx.qlmc like '%'||#{lx.qlmc}||'%'
 	
 	
 		and lx.akjfl like #{lx.akjfl}||'%'
 	 group by jh.id)

	select decode(jh.sbzt,0,'未上报',decode(jh.spzt,0,'上报待审批','已审批')) v_0,jh.sbnf v_1,to_char(jh.jhkgsj,'yyyy-mm-dd') v_2,to_char(jh.jhwgsj,'yyyy-mm-dd') v_3,lx.gydw v_4,lx.xzqhmc v_5,lx.lxbm v_6,lx.lxmc v_7,lx.qlbh v_8,lx.qlmc v_9,jh.pfztz v_10 
	from plan_wqgz jh
	left join sck_wqgz sc on (jh.sckid = sc.sckid)
	left join xmk_wqgz lx on (sc.xmkid = lx.id) where 1=1
	
		and jh.jh_sbthcd=#{jh.jh_sbthcd}
	
	
		and (jh.jh_sbthcd=#{jh.jh_sbthcd} or jh.jh_sbthcd=#{jh.jh_sbthcd}+2)
	
 	
 		and jh.sbnf=#{jh.sbnf}
 	
 	
 		and jh.sbzt=#{jh.sbzt}
 	
 	
 		and jh.kgzt=#{jh.kgzt}
 	
 	
 		and jh.jgzt=#{jh.jgzt}
 	
 	
 		and jh.spzt=#{jh.spzt}
 	
 	
		and sc.bzls=#{jh.sfylsjl}
	
 	
 		and ${lx.gydwbm}
 	
 	
 		and ${lx.xzqhdm}
 	
 	
 		and lx.lxmc like '%'||#{lx.lxmc}||'%'
 	
 	
 		and lx.lxbm like #{lx.lxbm}||'%'
 	
 	
 		and lx.jsdj = #{lx.jsjd}
 	
 	
 		and lx.qlmc like '%'||#{lx.qlmc}||'%'
 	
 	
 		and lx.akjfl like #{lx.akjfl}||'%'
 	
	order by jh.sbnf desc

mapper.update:
update plan_wqgz set sbnf=#{sbnf}
,jhkgsj=#{jhkgsj}
,jhwgsj=#{jhwgsj}
,xdsj=#{xdsj}
,pfsj=#{pfsj}
,jhxdwh=#{jhxdwh}
,sjdw=#{sjdw}
,sjpfdw=#{sjpfdw}
,pfwh=#{pfwh}
,pfztz=#{pfztz}
,jhsybzje=#{jhsybzje}
,jhsydfzcje=#{jhsydfzcje}
,sfsqablbz=#{sfsqablbz}
,ablbzsqwh=#{ablbzsqwh}
,bz=#{bz}
where id=#{id}

update SCK_WQGZ set sckid=#{sckid}
,scqlqk=#{scqlqk}
,scqlqc=#{scqlqc}
,faspsj=#{faspsj}
,fapgdw=#{fapgdw}
,fascdw=#{fascdw}
,spwh=#{spwh}
,tzgs=#{tzgs}
,jsxz=#{jsxz}
,jsnr=#{jsnr}
,scbz=#{scbz}
where sckid=#{sckid}

mapper.select:
		select jh.id,jh.sbnf,lx.qlmc,lx.qlzxzh from plan_wqgz jh left join sck_wqgz sc on (jh.sckid=sc.sckid) 
    	left join xmk_wqgz lx on (sc.xmkid=lx.id) where (lx.qlbh=#{qlbh} or
    	(lx.lxbm=#{lxbm} and lx.qlzxzh=#{qlzxzh})) and jh.id!=#{id}
