select c.v_0,c.v_1,c.v_2,c.v_3,c.v_4,c.v_5, c.v_6,round((c.v_2+c.v_3)/c.v_1,3)*100 v_7,c.v_8,fn_ReturnFllx(c.v_8) v_9 from ( select a v_0,sum(cd) v_1,sum(decode(pd,'优',cd,'0')) v_2,sum(decode(pd,'良',cd,'0')) v_3,sum(decode(pd,'中',cd,'0')) v_4, sum(decode(pd,'次',cd,'0')) v_5,sum(decode(pd,'差',cd,'0')) v_6,round(avg(MQI1),2) v_8 from( select 'MQI' a,sum(zdzh-qdzh) cd,fn_ReturnFllx(mqi) pd,'1' b,SUM(mqi*(zdzh-qdzh))/SUM((zdzh-qdzh)) MQI1 from LKPD_MXB_DATA_2014 t left join lkpd_mxb2014 m on t.mxb_id=m.id where jsdj!='高速'
and lxbh = #{lkmxb.lxbh}
and jcfx = #{lkmxb.jcfx}
and to_number(zdzh) >= to_number(#{lkmxb.qdzh})
and to_number(qdzh) <= to_number(#{lkmxb.zdzh})
and m.tbnf=#{lkmxb.tbnf}
and jcfx != '下行'
group by fn_ReturnFllx(mqi) union all select 'PQI' a,sum(zdzh-qdzh) cd,fn_ReturnFllx(pqi) pd,'2' b,SUM(pqi*(zdzh-qdzh))/SUM((zdzh-qdzh)) MQI1 from LKPD_MXB_DATA_2014 t left join lkpd_mxb2014 m on t.mxb_id=m.id where jsdj!='高速'
and lxbh = #{lkmxb.lxbh}
and jcfx = #{lkmxb.jcfx}
and to_number(zdzh) >= to_number(#{lkmxb.qdzh})
and to_number(qdzh) <= to_number(#{lkmxb.zdzh})
and m.tbnf=#{lkmxb.tbnf}
and jcfx != '下行'
group by fn_ReturnFllx(pqi)
union all select 'SCI' a,sum(zdzh-qdzh) cd,fn_ReturnFllx(sci) pd,'3' b,SUM(sci*(zdzh-qdzh))/SUM((zdzh-qdzh)) MQI1 from LKPD_MXB_DATA_2014 t left join lkpd_mxb2014 m on t.mxb_id=m.id where jsdj!='高速'
and lxbh = #{lkmxb.lxbh}
and jcfx = #{lkmxb.jcfx}
and to_number(zdzh) >= to_number(#{lkmxb.qdzh})
and to_number(qdzh) <= to_number(#{lkmxb.zdzh})
and m.tbnf=#{lkmxb.tbnf}
and jcfx != '下行'
group by fn_ReturnFllx(sci) union all select 'BCI' a,sum(zdzh-qdzh) cd,fn_ReturnFllx(bci) pd,'4' b,SUM(bci*(zdzh-qdzh))/SUM((zdzh-qdzh)) MQI1 from LKPD_MXB_DATA_2014 t left join lkpd_mxb2014 m on t.mxb_id=m.id where jsdj!='高速'
and lxbh = #{lkmxb.lxbh}
and jcfx = #{lkmxb.jcfx}
and to_number(zdzh) >= to_number(#{lkmxb.qdzh})
and to_number(qdzh) <= to_number(#{lkmxb.zdzh})
and m.tbnf=#{lkmxb.tbnf}
and jcfx != '下行'
group by fn_ReturnFllx(bci) union all select 'TCI' a,sum(zdzh-qdzh) cd,fn_ReturnFllx(tci) pd,'5' b,SUM(tci*(zdzh-qdzh))/SUM((zdzh-qdzh)) MQI1 from LKPD_MXB_DATA_2014 t left join lkpd_mxb2014 m on t.mxb_id=m.id where jsdj!='高速'
and lxbh = #{lkmxb.lxbh}
and jcfx = #{lkmxb.jcfx}
and to_number(zdzh) >= to_number(#{lkmxb.qdzh})
and to_number(qdzh) <= to_number(#{lkmxb.zdzh})
and m.tbnf=#{lkmxb.tbnf}
and jcfx != '下行'
group by fn_ReturnFllx(tci) ) group by a,b order by b ) c

