select t.xzqhdm,t.lc,round(t.zxbz+t.gdbz+t.sdbz,2) bzzj,l.name xzqh from
    (
	  select xzqhdm,sum(cd) lc,sum(cd)*0.3*70 zxbz,
	    sum(decode(gldj,'G',cd*lk,0))*0.7*26*0.7 gdbz,sum(decode(gldj,'S',cd*lk,0))*0.7*21*0.7 sdbz
		from wh_lkpd_data_ds  where xzqhdm is not null
		
		and lxbh in ${lxbm}
	    
	    
		and (${mqiStr})
	    
	    
		and pd in ${mqi}
	    
	    
		and gldj=#{xzdj}
	    
		 group by xzqhdm order by xzqhdm
	 ) t 
	 left join xtgl_xzqh l on t.xzqhdm=l.id

select t.lxbh lxbm,t.lc,round(t.zxbz+t.gdbz+t.sdbz,2) bzzj from 
    (
	 select lxbh,sum(cd) lc,sum(cd)*0.3*70 zxbz,
	  sum(decode(gldj,'G',cd*lk,0))*0.7*26*0.7 gdbz,sum(decode(gldj,'S',cd*lk,0))*0.7*21*0.7 sdbz
	  from wh_lkpd_data_ds  
	  where 1=1
	  
		and lxbh in ${lxbm}
	  
	  
		and (${mqiStr})
	  
	  
		and pd in ${mqi}
	  
	   
		and gldj=#{xzdj}
	    
	  group by lxbh order by lxbh
	 ) t
