mapper.select:
 	select * from (
 		select rownum num ,t.*,decode(sbthcd,#{sbthcd},'未上报',sbzt) sbzt2 from xmk_wqgz t where 1=1 and SFYLRBWQK='是'
 			and  sbthcd <=#{sbthcd}
 		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
 			and qlmc like '%'||(#{qlmc})||'%'
 		
 		
 			and xmnf in (${xmnf})
 		
 		
 			${gldj}
 		
 		
 			${jsdj}
 		
 		
 			and xmtype like '%'||(#{xmtype})||'%'
 		
 		
 			and decode(sbthcd,#{sbthcd},'未上报',sbzt) like '%'||(#{sbzt})||'%'
 		

 		
 			${akjfl}
 		
 		
 			and lxbm like '%'||(#{lxbm})||'%'
 		
 		
 			and qlbh like '%'||(#{qlbh})||'%'
 		
 		
 			${tsdq}
 		
 			) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows} 
 			order by decode(sbthcd,#{sbthcd},'未上报',sbzt),shzt desc

 		select count(*) from xmk_wqgz t where 1=1 and SFYLRBWQK='是'
 			and  sbthcd <=#{sbthcd}
 		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
 			and qlmc like '%'||(#{qlmc})||'%'
 		
 		
 			and xmnf in (${xmnf})
 		
 		
 			${gldj}
 		
 		
 			${jsdj}
 		
 		
 			and xmtype like '%'||(#{xmtype})||'%'
 		
 		
 			and decode(sbthcd,#{sbthcd},'未上报',sbzt) like '%'||(#{sbzt})||'%'
 		

 		
 			${akjfl}
 		
 		
 			and lxbm like '%'||(#{lxbm})||'%'
 		
 		
 			and qlbh like '%'||(#{qlbh})||'%'
 		
 		
 			${tsdq}

select count(*) from xmk_wqgz t left join (select count(*) jhsl,a3.id from plan_wqgz a1 left join sck_wqgz a2 on a1.sckid=a2.sckid left join xmk_wqgz a3 on a2.xmkid=a3.id group by a3.id) t1 on t.id=t1.id where 1=1 and sbthcd <=#{sbthcd} and gydwbm like '1%' and xxcsj='是' and sfsc is null
${gydw}
${xzqhdm}
and lxmc like '%'||(#{lxmc})||'%'
and qlmc like '%'||(#{qlmc})||'%'
and xmnf in (${xmnf})
and xmtype like '%'||(#{xmtype})||'%'
and decode(sbthcd,#{sbthcd},'未上报',sbzt) like '%'||(#{sbzt})||'%'
and pddj like '%'||(#{jsdj})||'%'
${akjfl}
and lxbm like '%'||(#{lxbm})||'%'
and qlbh like '%'||(#{qlbh})||'%'
${tsdq}
and sfylrbwqk like '%'||(#{sfylrbwqk})||'%'
and decode(t1.jhsl,null,'无','有') like #{bzls}||'%'
${gldj}

mapper.update:
update xmk_wqgz set qlmc=#{qlmc} ,qlbh=#{qlbh},xzqhmc=#{xzqhmc},xzqhdm=#{xzqhdm},lxmc=#{lxmc},lxbm=#{lxbm},
	gydw=#{gydw} ,xjgjnd=#{xjgjnd},jsdj=#{jsdj},pddj=#{pddj},qlzxzh=#{qlzxzh},qlkd=#{qlkd},qlqc=#{qlqc},kjzc=#{kjzc},
	dkzdkj=#{dkzdkj},akjfl=#{akjfl},sbjgxs=#{sbjgxs},bhnr=#{bhnr},bz=#{bz},xmnf=#{xmnf},xmtype=#{xmtype}
	where id=#{id}

mapper.select:
 	select * from (
 		select rownum num ,t.* from xmk_wqgz t where sbthcd=7 and SFYLRBWQK='是'
			and sbthcd>=#{sbthcd}
 		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
 			and qlmc like '%'||(#{qlmc})||'%'
 		
 		
 			and xmnf in (${xmnf})
 		
 		
 			and xmtype like '%'||(#{xmtype})||'%'
 		
 		
 			and shzt like '%'||(#{shzt})||'%'
 		
 		
 			${jsdj}
 		 
 		
 			${gldj}
 		 
 		
 			${akjfl}
 		
 		
 			and lxbm like '%'||(#{lxbm})||'%'
 		
 		
 			and qlbh like '%'||(#{qlbh})||'%'
 		
 		
 			${tsdq}
 		
 			) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows} 
 			order by shzt desc

 		select count(*) from xmk_wqgz t where sbthcd=7 and SFYLRBWQK='是'
			and sbthcd>=#{sbthcd}
 		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
 			and qlmc like '%'||(#{qlmc})||'%'
 		
 		
 			and xmnf in (${xmnf})
 		
 		
 			and xmtype like '%'||(#{xmtype})||'%'
 		
 		
 			and shzt like '%'||(#{shzt})||'%'
 		
 		
 			${jsdj}
 		 
 		
 			${gldj}
 		 
 		
 			${akjfl}
 		
 		
 			and lxbm like '%'||(#{lxbm})||'%'
 		
 		
 			and qlbh like '%'||(#{qlbh})||'%'
 		
 		
 			${tsdq}

   	select F114 jsdj from GPSROAD where 
   		roadcode=#{lxbm}
		and roadstart  <=#{qlzxzh}
     	and roadends >#{qlzxzh}

 	select distinct x.name from xtgl_xzqhtsdq t,xtgl_tsdq x where t.xzqhdm in(
	select distinct substr(roadcode,-6) from gpsroad where substr(roadcode,1,length(roadcode)-6) =#{lxbm}
	and roadstart<=#{zdzh} and roadends>=#{qdzh}) and x.id=t.parent
