mapper.select:
 	select * from (
 		select rownum num ,t.*,decode(sbthcd,#{sbthcd},'未上报',sbzt) sbzt2 from xmk_zhfz t where 1=1
 			and  sbthcd <=#{sbthcd}
 		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
 			and xmnf in (${xmnf})
 		
 		
 			and xmtype like '%'||(#{xmtype})||'%'
 		
 		
 			and decode(sbthcd,#{sbthcd},'未上报',sbzt) like '%'||(#{sbzt})||'%'
 		
 		
 			${jsdj}
 		
 		
 			and lxbm like #{lxbm}||'%'
 		
 		
 			${gldj}
 		
 		
 			${tsdq}
 		
 		
 			and xmklx like '%'||(#{xmklx})||'%'
 		
 			) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows} 
 			order by decode(sbthcd,#{sbthcd},'未上报',sbzt),shzt desc

	select count(*) from xmk_zhfz where 1=1
			and  sbthcd <=#{sbthcd}
 		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
 			and xmnf in (${xmnf})
 		
 		
 			and xmtype like '%'||(#{xmtype})||'%'
 		
 		
 			and decode(sbthcd,#{sbthcd},'未上报',sbzt) like '%'||(#{sbzt})||'%'
 		
 		
 			${jsdj}
 		
 		
 			and lxbm like #{lxbm}||'%'
 		
 		
 			${gldj}
 		
 		
 			${tsdq}
 		
 		
 			and xmklx like '%'||(#{xmklx})||'%'

mapper.update:
 	update xmk_zhfz set lxbm=#{lxbm},lxmc=#{lxmc},gydw=#{gydw},qdzh=#{qdzh},zdzh=#{zdzh},
 	qzlc=#{qzlc},xzqhdm=#{xzqhdm},xzqhmc=#{xzqhmc},gjxjnd=#{gjxjnd},lxjsdj=#{lxjsdj},yhlc=#{yhlc},xmnf=#{xmnf},xmtype=#{xmtype},
 	yhnr=#{yhnr},bz=#{bz}
 	
		,xmklx=#{xmklx}
	
	
	,lsjl=#{lsjl}
	
	
	,lsxmxx=#{lsxmxx}
	
    where id=#{id}

