mapper.select:
 	select
	shzt v_0,gydw v_1,xzqhmc v_2,lxbm v_3,lxmc v_4,qdzh v_5,zdzh v_6,qzlc v_7,qzlc v_8,yhlc v_9,gjxjnd v_10,xmnf v_11
 	from xmk_zhfz
 	where sbthcd=7
			and sbthcd>=#{sbthcd}
		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
