mapper.select:
 	select * from (
 		select rownum num ,t.*,decode(sbthcd,#{sbthcd},'未上报',sbzt) sbzt2 from xmk_zhfz t where 1=1
 			and  sbthcd <=#{sbthcd}
 		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
 			and xmnf like '%'||(#{xmnf})||'%'
 		
 		
 			and xmtype like '%'||(#{xmtype})||'%'
 		
 		
 			and decode(sbthcd,#{sbthcd},'未上报',sbzt) like '%'||(#{sbzt})||'%'
 		
 		
 			and lxjsdj like '%'||(#{lxjsdj})||'%'
 		
 		
 			and lxbm like #{lxbm}||'%'
 		
 		
 			and lxbm like #{bz}||'%'
 		
 		
 			and tsdq like '%'||(#{tsdq})||'%'
 		
 			) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows} 
 			order by decode(sbthcd,#{sbthcd},'未上报',sbzt),shzt desc

mapper.update:
update xmk_zhfz set lxbm=#{lxbm},lxmc=#{lxmc},gydw=#{gydw},qdzh=#{qdzh},zdzh=#{zdzh},
qzlc=#{qzlc},xzqhdm=#{xzqhdm},xzqhmc=#{xzqhmc},gjxjnd=#{gjxjnd},lxjsdj=#{lxjsdj},yhlc=#{yhlc},xmnf=#{xmnf},xmtype=#{xmtype},
yhnr=#{yhnr},bz=#{bz} where id=#{id}

mapper.select:
 	select * from (
 		select rownum num ,t.* from xmk_zhfz t where sbthcd=7
			and sbthcd>=#{sbthcd}
 		
			${gydw}
		
		
			${xzqhdm}
		
 		
 			and lxmc like '%'||(#{lxmc})||'%'
 		
 		
 			and xmnf like '%'||(#{xmnf})||'%'
 		
 		
 			and xmtype like '%'||(#{xmtype})||'%'
 		
 		
 			and shzt like '%'||(#{shzt})||'%'
 		
 		
 			and lxjsdj like '%'||(#{lxjsdj})||'%'
 		
 		
 			and lxbm like #{lxbm}||'%'
 		
 		
 			and lxbm like #{bz}||'%'
 		
 		
 			and tsdq like '%'||(#{tsdq})||'%'
 		
 			) t1 where t1.num <=(#{page} * #{rows}) and t1.num>(#{page}-1)*#{rows} 
 			order by shzt desc

