mapper.insert:
 			insert into plan_af(sckid,jhnf,jh_sbthcd,pfztz,jhsybbzje,jhsydfzczj,sfsqablbz,ablbzsqwh,pfwh,pfsj,jhzlc,jhyhlc,sjpfdw) select id,jhnf,decode(substr(t.gydwdm,0,1),'2','2',decode(substr(t.gydwdm,-1),'0','2','0')),
			t.cztzgs,t.nsqbbz,(t.cztzgs-t.nsqbbz),decode(t.sfsqablbz,1,'是','否'),t.ablbzsqwh,t.spwh,REPLACE( t.faspsj, '/', '-'),t.czzlc,t.yhlc,t.fascdw
			 from SCK_AQSMFH t where id=#{id}

mapper.select:
select rownum v_0,t.ZSZT v_1,t.roadcode v_2,t.ROADNAME v_3,t.THROADSTART v_4, t.THROADEND v_5,t.JSDJ v_6,t.GLDJ v_7,t.FANGX v_8,t.PCSJ v_9,t.LDFL v_10, t.UNIT v_11,t.DIST v_12,t.TZGS v_13,t.JHNF v_14, decode(sbthcd, '7', '已上报', 7, '未上报', sbzt) v_15 from XMK_AQSMFH t left join (select count(*) jhsl, a4.id from plan_af a1 left join sck_aqsmfh a2 on a1.sckid = a2.id left join sck_aqsmfh_ld a3 on a2.xmbm = a3.xmbm left join xmk_aqsmfh a4 on a3.xmkid = a4.id group by a4.id) t1 on t.id = t1.id where 1 = 1 and to_number(sbthcd) <=to_number(#{sbthcd})
${unitcode}
${distcode}
and roadname like '%'||(#{roadname})||'%'
and jhnf like '%'||(#{jhnf})||'%'
and decode(sbthcd,'7','已上报',#{sbthcd},'未上报',sbzt) like '%'||(#{sbzt})||'%'
${jsdj}
${gldj}
and roadcode like '%'||(#{roadcode})||'%'
${tsdq}
and decode(t1.jhsl,null,'无','有') like #{bzls}||'%'
order by v_0 asc

