select * from (	
	select rownum num,t.* from (
		select j.*,l.ghlxbh,l.lsjl,l.gydw,lx.lc,sl.zlc,sl.xmsl from jhsh_xj_qx j left join lxsh_xj l on j.xmbm=l.xmbm
		left join (select xmid,lc from lxsh_lx where jdbs='2' and xmid like '%3___' and sffirst=1) lx on j.xmbm = lx.xmid
		left join (select xmid,count(xmid) xmsl,decode(substr(xmid,11,1),1,(nvl(sum(jhyilc),0)+nvl(sum(jherlc),0)+nvl(sum(jhsanlc),0)+nvl(sum(jhsilc),0)+nvl(sum(jhdwlc),0)+nvl(sum(jhwllc),0)),(nvl(sum(yilc),0)+nvl(sum(erlc),0)+nvl(sum(sanlc),0)+nvl(sum(silc),0)+nvl(sum(dwlc),0)+nvl(sum(wllc),0))) zlc from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid
		where 1=1
		and ${jhsh.xzqhdm}
		
			and j.xmbm in (select distinct xmid from lxsh_lx lx where lx.lxbm like '%'||#{jhsh.ghlxbh}||'%')
		
		
			and j.xmbm in (select distinct xmid from lxsh_lx lx where ${jhsh.ylxbh} and xmid like '%3___' and jdbs='2')
		
		and l.tsdq like '%'||#{jhsh.tsdq}||'%'
		and ${jhsh.xmbm}
		and j.xmmc like '%'||#{jhsh.xmmc}||'%'
		and j.xdzt=${jhsh.xdzt}
		and ${jhsh.jsdj}
		and l.lsjl=#{jhsh.lsjl}
		order by j.xdzt,j.xmbm desc
	) t 
) 

	where  num>(${page}-1)*${rows} and num<=${page}*${rows}

	select * from (	
		select rownum num,t.* from (
			select j.*,lxbm,qdzh1,zdzh1,ghlxbm,ghlxmc,ghqdzh,ghzdzh,gxlxbm,gxqdzh,gxzdzh,to_char(lx.lc,'fm9999999990.000') jhlc,sl.zlc,sl.xmsl xmsl,lxg.ntz tz,lxg.jsdj,cb.sjpfwh,lxg.tsdq,lxg.lxmc,wh.bcxdwh bcxdwh,wh.bcxdsj bcxdsj,wh.bcztz,wh.bccgs,wh.bcstz from jhxdyh j  left join (select xmbm,sjpfwh from cbsj_yhdzx) cb on j.xmbm=cb.xmbm  left join xmsq_yhdzx lxg on j.xmbm=lxg.xmbm left join (select xmid xmbm,min(lx.xzqhdm) xzqhdm,min(lx.gydw) gydw from lxsh_lx lx group by lx.xmid) l on j.xmbm=l.xmbm
            left join (select  xmid,jsxz,
            (case when substr(xmid,11,1)=1 then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))
            
            else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) end) lc,ghlxbm,ghlxmc,ghqdzh,ghzdzh,gxlxbm,gxqdzh,gxzdzh,lxbm,lxmc,qdzh qdzh1,zdzh zdzh1
            from lxsh_lx where jdbs='2' and sffirst='1' ) lx 
            on j.xmbm=lx.xmid
            left join (select xmid,count(xmid) xmsl,decode(substr(xmid,11,1),1,(nvl(sum(jhyilc),0)+nvl(sum(jherlc),0)+nvl(sum(jhsanlc),0)+nvl(sum(jhsilc),0)+nvl(sum(jhdwlc),0)+nvl(sum(jhwllc),0)),(nvl(sum(yilc),0)+nvl(sum(erlc),0)+nvl(sum(sanlc),0)+nvl(sum(silc),0)+nvl(sum(dwlc),0)+nvl(sum(wllc),0))) zlc from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid
            left join (select xmid,sum(jhlc) zjhlc,replace(WMSYS.WM_CONCAT(lxbm),',',',') ghlxbmtj,replace(WMSYS.WM_CONCAT(ghxlxbm),',',',') ghxlxbmtj,
            replace(WMSYS.WM_CONCAT(jsjsdj),',',',') jsjsdjtj,
            replace(WMSYS.WM_CONCAT(xjsdj),',',',') xjsdjtj,
            replace(WMSYS.WM_CONCAT(lxmc),',',',') ghlxmctj,replace(WMSYS.WM_CONCAT(ghxlxmc),',',',') ghxlxmctj
            from (select  xmid,jsxz,
            (case when substr(xmid,11,1)=1 then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))
            
            else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0))
            end) jhlc,lxbm,lxmc,(case when substr(xmid,11,1)=3 then xjlxbm 
            else ''
            end) ghxlxbm,'' ghxlxmc,jsjsdj,xjsdj
            from LXSH_LX  where jdbs='2' ) group by xmid) lx2 on j.xmbm=lx2.xmid
             
	           left join (select j.xmid,
                sum(j.btzzj) bcxd,
              	replace(WMSYS.WM_CONCAT(j.jhxdwh),',',',') bcxdwh,
              	replace(WMSYS.WM_CONCAT(j.xdnf),',',',') bcxdsj,
             	sum(j.ztz) bcztz,
             	sum(j.rys) + sum(j.dk) + sum(j.jl) bcstz,
            	sum(j.btzzj) bccgs
          		from jhxdyh g
          		left join plan_zjxd j
          		on g.xmbm = j.xmid
         		where 1 = 1 ${jhxdwh}  group by j.xmid) wh on j.xmbm=wh.xmid
	        
              where 1=1 
			and ${xzqhdm}
			
				and ghlxbmtj like '%'||#{ghlxbh}||'%'
			
			
			
				and ghlxmctj like '%'||#{lxmc}||'%'
			
			
			
			
			and (j.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxbm like '%'||#{ghxlxbm}||'%')
			or j.xmbm in (select distinct xmid from lx_clsd where ghlxbm like '%'||#{ghxlxbm}||'%')
			)
		
		
			and (j.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxmc like '%'||#{ghxlxmc}||'%')
			or j.xmbm in (select distinct xmid from lx_clsd where ghlxmc like '%'||#{ghxlxmc}||'%')
			)
		
			
				and substr(j.xmbm,0,4) in (${xmnf})
			
			
				${tsdq}
			
			
				and ${xzdj}
			
			and j.xdzt=${shzt}
			and j.sbzt=${sbzt}
			and j.xmmc like '%'||#{xmmc}||'%'
			and ${jsdj}
			and j.lsjl=#{lsjl}
			${xmlx1}
			${jhxdwh}
			
				${zjly}
			
			
				
			
			
				and sfsycgs = #{sfsycgs}
			
			
				${xdzttj}
			
			order by j.zydpx,j.zydpx desc,j.xdzt,j.xmbm desc
			
		) t 
	) 
	
		where  num>(${page}-1)*${rows} and num<=${page}*${rows}

select * from (	
	select rownum num,t.* from (
		select j.*,c.kgsj,c.wgsj,c.gq,l.lsjl,l.gydw,l.qdmc,l.zdmc,l.jsdj,sl.zlc,sl.xmsl 
		from jhsh_yhdzx_qx j left join cbsj_yhdzx c on j.xmbm=c.xmbm left join xmsq_yhdzx l on c.xmbm = l.xmbm
		left join (select xmid,count(xmid) xmsl,decode(substr(xmid,11,1),1,(nvl(sum(jhyilc),0)+nvl(sum(jherlc),0)+nvl(sum(jhsanlc),0)+nvl(sum(jhsilc),0)+nvl(sum(jhdwlc),0)+nvl(sum(jhwllc),0)),(nvl(sum(yilc),0)+nvl(sum(erlc),0)+nvl(sum(sanlc),0)+nvl(sum(silc),0)+nvl(sum(dwlc),0)+nvl(sum(wllc),0))) zlc from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid
		where 1=1
		
			and ${jhsh2.xzqhdm}
		
		
			and j.xmbm in (select distinct xmid from lxsh_lx lx where lx.lxbm like '%'||#{jhsh2.ghlxbh}||'%')
		
		
			and j.xmbm in (select distinct xmid from lxsh_lx lx where ${jhsh2.ylxbh} and xmid like '%4___' and jdbs='2')
		
		
			and l.lsjl=#{jhsh2.lsjl}
		
		
			and l.tsdq like '%'||#{jhsh2.tsdq}||'%'
		
		
			and ${jhsh2.jsdj}
		
		
			and ${jhsh2.xmbm}
		
		
			and j.xmmc like '%'||#{jhsh2.xmmc}||'%'
		
		
			and j.xdzt=${jhsh2.xdzt}
		
		order by j.xdzt,j.xmbm desc
	) t 
) 

	where  num>(${page}-1)*${rows} and num<=${page}*${rows}

select * from ( select rownum num,t.* from ( select j.*,lxbm,qdzh1,zdzh1,ghlxbm,ghlxmc,ghqdzh,ghzdzh,gxlxbm,gxqdzh,gxzdzh,to_char(lx.lc,'fm9999999990.000') jhlc,sl.zlc,sl.xmsl xmsl,lxg.jsdj,lxg.xzqh xzqhmc,cb.sjpfwh,lxg.tsdq,lxg.lxmc,wh.bcxdwh bcxdwh,wh.bcxdsj bcxdsj,wh.bcztz,wh.bccgs,wh.bcstz from jhxdsh j left join (select xmbm,sjpfwh from cbsj_sh) cb on j.xmbm=cb.xmbm left join xmsq_sh lxg on j.xmbm=lxg.xmbm left join (select xmid xmbm,min(lx.xzqhdm) xzqhdm,min(lx.gydw) gydw from lxsh_lx lx where jdbs='2' group by lx.xmid) l on j.xmbm=l.xmbm left join (select xmid,jsxz, (case when substr(xmid,11,1)=1 then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))
else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) end) lc,ghlxbm,ghlxmc,ghqdzh,ghzdzh,gxlxbm,gxqdzh,gxzdzh,lxbm,lxmc,qdzh qdzh1,zdzh zdzh1 from lxsh_lx where jdbs='2' and sffirst='1' ) lx on j.xmbm=lx.xmid left join (select xmid,count(xmid) xmsl,decode(substr(xmid,11,1),1,(nvl(sum(jhyilc),0)+nvl(sum(jherlc),0)+nvl(sum(jhsanlc),0)+nvl(sum(jhsilc),0)+nvl(sum(jhdwlc),0)+nvl(sum(jhwllc),0)),(nvl(sum(yilc),0)+nvl(sum(erlc),0)+nvl(sum(sanlc),0)+nvl(sum(silc),0)+nvl(sum(dwlc),0)+nvl(sum(wllc),0))) zlc from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid left join (select xmid,sum(jhlc) zjhlc,replace(WMSYS.WM_CONCAT(lxbm),',',',') ghlxbmtj,replace(WMSYS.WM_CONCAT(ghxlxbm),',',',') ghxlxbmtj, replace(WMSYS.WM_CONCAT(jsjsdj),',',',') jsjsdjtj, replace(WMSYS.WM_CONCAT(xjsdj),',',',') xjsdjtj, replace(WMSYS.WM_CONCAT(lxmc),',',',') ghlxmctj,replace(WMSYS.WM_CONCAT(ghxlxmc),',',',') ghxlxmctj from (select xmid,jsxz, (case when substr(xmid,11,1)=1 then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))
else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) end) jhlc,lxbm,lxmc,(case when substr(xmid,11,1)=3 then xjlxbm else '' end) ghxlxbm,'' ghxlxmc,jsjsdj,xjsdj from LXSH_LX where jdbs='2' ) group by xmid) lx2 on j.xmbm=lx2.xmid
left join (select j.xmid, sum(j.btzzj) bcxd, replace(WMSYS.WM_CONCAT(j.jhxdwh),',',',') bcxdwh, replace(WMSYS.WM_CONCAT(j.xdnf),',',',') bcxdsj, sum(j.ztz) bcztz, sum(j.rys) + sum(j.dk) + sum(j.jl) bcstz, sum(j.btzzj) bccgs from jhxdsh g left join plan_zjxd j on g.xmbm = j.xmid where 1 = 1 ${jhxdwh} group by j.xmid) wh on j.xmbm=wh.xmid
where 1=1 and ${xzqhdm}
and ghlxbmtj like '%'||#{ghlxbh}||'%'
and ghlxmctj like '%'||#{lxmc}||'%'
and (j.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxbm like '%'||#{ghxlxbm}||'%') or j.xmbm in (select distinct xmid from lx_clsd where ghlxbm like '%'||#{ghxlxbm}||'%') )
and (j.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxmc like '%'||#{ghxlxmc}||'%') or j.xmbm in (select distinct xmid from lx_clsd where ghlxmc like '%'||#{ghxlxmc}||'%') )
and substr(j.xmbm,0,4) in (${xmnf})
${tsdq}
and ${xzdj}
and j.xmmc like '%'||#{xmmc}||'%' and j.xdzt=${shzt} and j.sbzt=${sbzt} and ${jsdj} and j.lsjl=#{lsjl} ${jhxdwh}
${xdzttj}
order by j.zydpx,j.zydpx desc,j.xdzt,j.xmbm desc ) t )
where num>(${page}-1)*${rows} and num<=${page}*${rows}

	select * from (	
		select rownum num,t.* from (
			select decode(l_.sffirst,'1',j.xmbm,'') xmbm,decode(l_.sffirst,'1',j.xmmc,'') xmmc,decode(l_.sffirst,'1',j.xzqh,l_.xzqh) xzqh,j.jhkgsj kgsj,j.jhwgsj wgsj,decode(l_.sffirst,'1',j.lc,l_.lc) lc ,j.xdwh xdwh,j.xdsj,j.PFZTZ,j.SBZZJ,j.BBZZJ, l_.lxbm ylxbh ,l_.qdzh,l_.zdzh,l_.qdmc,l_.zdmc,sl.zlc,sl.xmsl xmsl,lxg.jsdj,lxg.xzqh xzqhmc,cb.sjpfwh,cb.gq,
			l_.lxmc,l_.ghlxbm,l_.ghlxmc,l_.ghqdzh,l_.ghzdzh,l_.gxlxbm,l_.gxqdzh,l_.gxzdzh
			 from jhxdsh j 
				left join (select xmbm,sjpfwh,gq from cbsj_sh) cb on j.xmbm=cb.xmbm 
				left join xmsq_sh lxg on j.xmbm=lxg.xmbm 
				left join (select xmid xmbm,min(lx.xzqhdm) xzqhdm,min(lx.gydw) gydw from lxsh_lx lx where jdbs='2' group by lx.xmid) l on j.xmbm=l.xmbm 
				left join (select xmid,jsxz, (case when substr(xmid,11,1)=1 then to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0)) 
				 else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) end) lc from lxsh_lx where jdbs='2' 
				and sffirst='1' ) lx on j.xmbm=lx.xmid left join (select xmid,count(xmid) xmsl,decode(substr(xmid,11,1),1,(nvl(sum(jhyilc),0)+nvl(sum(jherlc),0)+nvl(sum(jhsanlc),0)+nvl(sum(jhsilc),0)+nvl(sum(jhdwlc),0)+nvl(sum(jhwllc),0)),(nvl(sum(yilc),0)+nvl(sum(erlc),0)+nvl(sum(sanlc),0)+nvl(sum(silc),0)+nvl(sum(dwlc),0)+nvl(sum(wllc),0))) zlc from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid 
				left join (select xmid,sum(jhlc) zjhlc,replace(WMSYS.WM_CONCAT(lxbm),',',',') ghlxbmtj,replace(WMSYS.WM_CONCAT(ghxlxbm),',',',') ghxlxbmtj, 
				replace(WMSYS.WM_CONCAT(jsjsdj),',',',') jsjsdjtj, replace(WMSYS.WM_CONCAT(xjsdj),',',',') xjsdjtj, replace(WMSYS.WM_CONCAT(lxmc),',',',') ghlxmctj,
				replace(WMSYS.WM_CONCAT(ghxlxmc),',',',') ghxlxmctj from (select xmid,jsxz, (case when jsxsubstr(xmid,11,1)=1en to_char(nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)
				+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0))  else to_char(nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) end) jhlc,lxbm,lxmc,(case when substr(xmid,11,1)=3 then xjlxbm else '' end)
				 ghxlxbm,'' ghxlxmc,jsjsdj,xjsdj from LXSH_LX where jdbs='2' ) group by xmid) lx2 on j.xmbm=lx2.xmid 
				 right  join (select * from lxsh_lx lx where lx.jdbs='2') l_ on j.xmbm=l_.xmid 
              where 1=1
			and ${xzqhdm}
			
				and ghlxbmtj like '%'||#{ghlxbh}||'%'
			
			
				and ghxlxbmtj like '%'||#{ghxlxbm}||'%'
			
			
				and ghlxmctj like '%'||#{lxmc}||'%'
			
			
				and ghxlxmctj like '%'||#{ghxlxmc}||'%'
			
			
				and substr(j.xmbm,0,4) in (${xmnf})
			
			
			
				${tsdq}
			
			
				and ${xzdj}
			
			and j.xmmc like '%'||#{xmmc}||'%'
			and j.xdzt=${shzt}
			and j.sbzt=${sbzt}
			and ${jsdj}
			and j.lsjl=#{lsjl}
			${jhxdwh}
			
				${xdzttj}
			
			order by j.xdzt,j.xmbm desc,l_.sffirst desc
		) t 
	) 
	
		where  num>(${page}-1)*${rows} and num<=${page}*${rows}

select decode(l_.sffirst,'1',j.XMMC,'') xmmc,decode(l_.sffirst,'1',j.XMBM,'') xmbm,j.XZQH,j.JHKGSJ kgsj,j.JHWGSJ wgsj,j.XDWH,j.XDSJ,j.ZTZ,j.SYSBBZJ,j.YQDBCGS,j.YLXBH,l_.qdmc,l_.zdmc,l_.qdzh,l_.zdzh,l_.lc ,j.MCLMLX,j.MCLX,j.MCSL,j.MCJE,j.JCLX,j.JCSL,j.MCSL,j.XFCSL,j.XFCJE,j.BXSL,j.BXJE,j.GFCD,j.GFJE,j.LLCL, lx.lc, jhlc,sl.xmsl xmsl,lxg.ntz tz,lxg.jsdj,cb.sjpfwh,cb.gq, l_.lxmc,l_.ghlxbm,l_.ghlxmc,l_.ghqdzh,l_.ghzdzh,l_.gxlxbm,l_.gxqdzh,l_.gxzdzh from jhxdyh j left join (select xmbm,sjpfwh,gq from cbsj_yhdzx) cb on j.xmbm=cb.xmbm left join xmsq_yhdzx lxg on j.xmbm=lxg.xmbm left join (select xmid xmbm,min(lx.xzqhdm) xzqhdm,min(lx.gydw) gydw from lxsh_lx lx where jdbs='2' group by lx.xmid) l on j.xmbm=l.xmbm left join (select xmid,jsxz, (case when substr(xmid,11,1)=1 then nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0) when substr(xmid,11,1)=3 then to_number(xjlc) else nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0) end) lc from lxsh_lx where jdbs='2' and sffirst='1' ) lx on j.xmbm=lx.xmid left join (select xmid,count(xmid) xmsl,decode(substr(xmid,11,1),1,(nvl(sum(jhyilc),0)+nvl(sum(jherlc),0)+nvl(sum(jhsanlc),0)+nvl(sum(jhsilc),0)+nvl(sum(jhdwlc),0)+nvl(sum(jhwllc),0)),(nvl(sum(yilc),0)+nvl(sum(erlc),0)+nvl(sum(sanlc),0)+nvl(sum(silc),0)+nvl(sum(dwlc),0)+nvl(sum(wllc),0))) zlc from lxsh_lx where jdbs='2' group by xmid) sl on j.xmbm=sl.xmid left join (select xmid,sum(jhlc) zjhlc,replace(WMSYS.WM_CONCAT(lxbm),',',',') ghlxbmtj,replace(WMSYS.WM_CONCAT(ghxlxbm),',',',') ghxlxbmtj, replace(WMSYS.WM_CONCAT(jsjsdj),',',',') jsjsdjtj, replace(WMSYS.WM_CONCAT(xjsdj),',',',') xjsdjtj, replace(WMSYS.WM_CONCAT(lxmc),',',',') ghlxmctj, replace(WMSYS.WM_CONCAT(ghxlxmc),',',',') ghxlxmctj from (select xmid,jsxz, (case when substr(xmid,11,1)=1 then nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+ nvl(jhdwlc,0)+nvl(jhwllc,0) when substr(xmid,11,1)=3 then to_number(xjlc) else to_number(lc) end) jhlc,lxbm,lxmc,(case when substr(xmid,11,1)=3 then xjlxbm else '' end) ghxlxbm,'' ghxlxmc,jsjsdj,xjsdj from LXSH_LX where jdbs='2' ) group by xmid) lx2 on j.xmbm=lx2.xmid right join (select * from lxsh_lx lx where jdbs='2') l_ on j.xmbm=l_.xmid where 1=1 and ${xzqhdm}
