			or j.xmbm in (select distinct xmid from lx_clsd where ghlxmc like '%'||#{ghxlxmc}||'%')
			)
		
			
				and substr(j.xmbm,0,4) in (${xmnf})
			
			
				${tsdq}
			
			
				and ${xzdj}
			
