			and ${jsdj}
			and j.lsjl=#{lsjl}
			${xmlx1}
			${jhxdwh}
			
				${zjly}
			
			
				
			
			
				and sfsycgs = #{sfsycgs}
			
			
				${xdzttj}
			
			order by j.xdzt,j.xmbm desc
		) t

