mapper.select:
select ylxbm ylxbm,nvl((case when to_number(yqdzh)<to_number(yzdzh) and to_number(Xqdzh)<${ghqdzh} then to_number(YQDZH)+${ghqdzh}-to_number(XQDZH) when to_number(yqdzh)<to_number(yzdzh) and to_number(Xqdzh)>${ghqdzh} then yqdzh when to_number(yqdzh)>to_number(yzdzh) and to_number(Xqdzh)<${ghqdzh} then ${ghqdzh}-to_number(xqdzh)+to_number(yzdzh) when to_number(yqdzh)>to_number(yzdzh) and to_number(Xqdzh)>${ghqdzh} then yzdzh end),0) qdzh, nvl((case when to_number(yqdzh)<to_number(yzdzh) and ${ghzdzh}<to_number(Xzdzh) then to_number(YZDZH)-to_number(XZDZH)+${ghzdzh} when to_number(yqdzh)<to_number(yzdzh) and ${ghzdzh}>to_number(Xzdzh) then yzdzh when to_number(yqdzh)>to_number(yzdzh) and ${ghzdzh}<to_number(Xzdzh) then ${ghzdzh}-to_number(xzdzh)+to_number(yqdzh) when to_number(yqdzh)>to_number(yzdzh) and ${ghzdzh}>to_number(Xzdzh) then yqdzh end ),0) zdzh , substr(ylxbm,0,4) as lxbm, nvl((case when Xqdzh<${ghqdzh} then ${ghqdzh} else xqdzh end ),0) xqdzh, nvl((case when ${ghzdzh}<Xzdzh then ${ghzdzh} else xzdzh end),0) xzdzh from ghlx t where t.xlxbm like #{ghlxbm} || '%' and t.Xqdzh<${ghzdzh} and t.Xzdzh>${ghqdzh}
and ${xzqhdm}
and ylxbm is not null and yqdzh is not null and yzdzh is not null

select distinct tab.* from( select distinct l.xmid,decode(substr(l.xmid,11,1),'1',(select xmmc from jhsh_sjgz where xmbm=l.xmid), '2',(select xmmc from jhsh_lmgz where xmbm=l.xmid), '3',(select xmmc from jhsh_xj where xmbm=l.xmid), '4',(select xmmc from jhsh_yhdzx where xmbm=l.xmid), '5',(select xmmc from jhsh_sh where xmbm=l.xmid) ) xmmc, l.ghlxbm lxbm,l.ghlxmc lxmc,l.qcqd qdzh,l.qczd zdzh,decode(l.lxbm, null, lx.lxbm, l.lxbm) ylxbm,decode(l.qdzh, null, lx.qdzh, l.qdzh) yqdzh,decode(l.zdzh, null, lx.zdzh, l.zdzh) yzdzh,lx.jsxz xjsdj,lx.xjsdj yjsdj,decode(substr(l.xmid,11,1),'1',lx.jsjsdj,lx.xjsdj) jsjsdj from lx_clsd l left join lxsh_lx lx on l.id=lx.id where 1=1 and lx.jdbs='2' and l.ghlxbm like '%'||#{lx.lxbm}||'%' and to_number(l.qczd) > ${lx.qdzh} and ${lx.zdzh} > to_number(l.qcqd) and lx.xmid!=#{lx.xmid} union all select distinct l.xmid,j.xmmc,decode(cl.ghlxbm,null,l.lxbm,cl.ghlxbm) lxbm,decode(cl.ghlxmc,null,l.lxmc,cl.ghlxmc) lxmc,decode(cl.qcqd,null,l.qdzh,cl.qcqd) qdzh,decode(cl.qczd,null,l.zdzh,cl.qczd) zdzh,decode(cl.lxbm, null, l.lxbm, cl.lxbm) ylxbm,decode(cl.qdzh, null, l.qdzh, cl.qdzh) yqdzh,decode(cl.zdzh, null, l.zdzh, cl.zdzh) yzdzh,j.gcfl xjsdj,l.xjsdj yjsdj,decode(substr(l.xmid,11,1),'1',l.jsjsdj,l.xjsdj) jsjsdj from lx_clsd cl right join lxsh_lx l on cl.id=l.id right join jhsh_yhdzx j on l.xmid=j.xmbm right join cbsj_yhdzx c on j.xmbm=c.xmbm where l.jdbs='2' and l.lxbm like '%'||#{lx.lxbm}||'%' and ( (to_number(l.zdzh) > ${lx.qdzh} and ${lx.zdzh} > to_number(l.qdzh)) or (to_number(cl.zdzh) > ${lx.qdzh} and ${lx.zdzh} > to_number(cl.qdzh)) )
