from jhxdyh j left join xmsq_yhdzx x on j.xmbm=x.xmbm left join cbsj_yhdzx c on j.xmbm=c.xmbm left join ( select p.xmid, sum(p.ztz) ztz, sum(p.rys) rys, sum(p.dk) dk, sum(p.btzzj) btz, sum(p.dfzc) dfzc, sum(p.yhdk) yhdk, sum(gz) + sum(sz) + sum(zq) dftz from plan_zjxd p where p.xdnf = #{xmnf} group by p.xmid ) xd on j.xmbm=xd.xmid left join( select xmid,decode(substr(xmid, 11, 1), '1', (sum(nvl(jhyilc, 0)) + sum(nvl(jherlc, 0)) + sum(nvl(jhsanlc, 0)) + sum(nvl(jhsilc, 0)) + sum(nvl(jhdwlc, 0)) + sum(nvl(jhwllc, 0))), (sum(nvl(yilc, 0)) + sum(nvl(erlc, 0)) + sum(nvl(sanlc, 0)) + sum(nvl(silc, 0)) + sum(nvl(dwlc, 0)) + sum(nvl(wllc, 0)))) zlc, decode(substr(xmid, 11, 1), '1', (sum(nvl(jhyilc, 0))), (sum(nvl(yilc, 0)))) yilc, decode(substr(xmid, 11, 1), '1', (sum(nvl(jherlc, 0))), (sum(nvl(erlc, 0)))) erlc, (sum(nvl(jhsanlc, 0))), (sum(nvl(sanlc, 0))) sanlc, (sum(nvl(jhsilc, 0))), (sum(nvl(silc, 0))) silc, min(jsxz) jsxz, min(xzqhdm) xzqhdm, min(gydw) gydw, min(lxbm) lxbm, min(lxmc) lxmc, min(jsjsdj) jsjsdj from lxsh_lx where jdbs = '2' group by xmid) l on j.XMBM=l.xmid where 1=1 and ${xzqhdm}
and ghlxbmtj like '%'||#{ghlxbh}||'%'
and ghlxmctj like '%'||#{lxmc}||'%'
and (j.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxbm like '%'||#{ghxlxbm}||'%') or j.xmbm in (select distinct xmid from lx_clsd where ghlxbm like '%'||#{ghxlxbm}||'%') )
