mapper.select:
select * from (
select rownum num ,t.*,t1.ghlxbh lxbh,t1.gydwdm,l.yilc,l.erlc,l.sanlc,l.silc,l.dwlc,l.wllc
from KXXYJ_SJGZ t Left join lxsh_sjgz t1 on t.xmbm=t1.xmbm left join lxsh_lx l on t1.xmbm=l.xmid where 1=1 and  t.sbthcd=7 and l.sffirst='1' and l.jdbs='1'

	${gydw}


	${xzqh}


	and t.xmmc like '%'||#{xmmc}||'%'


	and t1.xmnf like '%'||#{xmnf}||'%'


	and t.shzt like '%'||#{sbzt1}||'%'


	${tsdq}


	and t.jsjsdj like '%'||#{jsdj}||'%'


	and ghlxbh like #{gldj}||'%'


	and t.xmbm in (select distinct xmid from lxsh_lx lx where lx.lxbm like '%'||#{ghlxbh}||'%')


	and lxmc like '%'||#{lxmc}||'%'


	and (t.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxbm like '%'||#{ghlxbm}||'%')
	or t.xmbm in (select distinct xmid from lx_clsd where ghlxbm like '%'||#{ghlxbm}||'%')
	)


	and (t.xmbm in (select distinct xmid from lxsh_lx lx where lx.ghlxmc like '%'||#{ghlxmc}||'%')
	or t.xmbm in (select distinct xmid from lx_clsd where ghlxmc like '%'||#{ghlxmc}||'%')
	)

) t2 where t2.num <=(#{page} * #{rows}) and t2.num>(#{page}-1)*#{rows}
order by xmbm

