    select k.xmmc,x.xmbm,null,k.xzqh,k.qdzh,k.zdzh,k.jsdj,x.gcfl,k.kgny,k.wgny,x.gq,x.lc,x.jsfa 
    from xmsq_sh x left join kxxyj_sh k on x.xmbm=k.xmbm where x.xmbm in (${xmbm})
