    	replace(WMSYS.WM_CONCAT(lxmc),',',',') ghlxmctj,replace(WMSYS.WM_CONCAT(ghxlxmc),',',',') ghxlxmctj,'路面改造工程' xmlx1 
    	from WNJH_LX group by xmid) lx2 on t.id=lx2.xmid
		where 1=1
		
			${xzqh}
		
		
			and xmnf in (${xmnf})
		
		
			${tsdq}
		
		
			${jsdj}
		
		
