select * from ( select rownum num ,xmid xmbm,lxbm ghlxbh,t.*,decode(jsxz,'改建',nvl(jhyilc,0)+nvl(jherlc,0)+nvl(jhsanlc,0)+nvl(jhsilc,0)+nvl(jhdwlc,0)+nvl(jhwllc,0), nvl(yilc,0)+nvl(erlc,0)+nvl(sanlc,0)+nvl(silc,0)+nvl(dwlc,0)+nvl(wllc,0)) zjhlc from WNJH_LX t where 1=1 and sffirst!='1'
and xmid like '%'||#{xmbm}||'%'
) t1

